typedef struct s_content {
    union {
        PBinList      pbl;
        PCompactArray pca;
    } p;
    int bin_size;
    struct s_content *next;
} Content, *PContent;

PContent lists;


void compact_bin_list(void) {
    PContent p = lists;
    for (; p; p = p->next) {
        if (!p->p.pbl->data.shallow) {
            p->p.pca = list_to_compact_array(p->p.pbl);
        }
    }
}



