PBinList get_pbin_lists(PContent pc) {   
    if (pc->p.pbl) return pc->p.pbl;
    pc->p.pbl =  calloc(1,sizeof(BinList));
    pc->p.pbl->data.bin_size = pc->bin_size;
    pc->next = lists;
    lists = pc;
    return pc->p.pbl;
}

