    p->next_bin = pbl->last;  // usa isso para corrigir a realocacao

    //xprintf("BINLIST: new id_bin %d  %ld  %p\n", id_bin, value, pbl);
    //xprintf("Alocando... P=%p  Last=%p, LastNItems=%d\n",p, pbl->last,pbl->data.last_node_n_items);

